import React, {Component} from 'react';
class Todo extends Component{
	constructor(props)
	{
		super(props);
		this.state={
			item: "hello"
		}
	}
	render(){
		return (<div>

			<div className="header">Todo App </div>

			<div className="body"> </div>

			<div className="footer">
			<input type="text" value={this.state.item}/>
			<button> Add </button>
			</div>

			</div>);
	}
}
export default Todo;
